import React from 'react';
import styles from './Settings.module.css';
import Button from '../../ui/button/Button'; // Corrected path assuming Button is properly designed for actions

const Settings = () => {
    const handleClearCache = () => console.log('Clear cache action');
    const handleChangeTheme = () => console.log('Change theme action');
    const handleGetAuthorization = () => {
        const url = "https://www.last.fm/api/auth?api_key=053905e1fc8b0de378dc341a24ec68c7&cb=http://yourapp.com/callback";
        const authWindow = window.open(url, "_blank", "width=800,height=600"); // Opens the URL in a new window

        const pollTimer = window.setInterval(() => {
            try {
                if (authWindow?.closed) {
                    window.clearInterval(pollTimer);
                    console.log('Authorization window closed by user.');
                } else if (authWindow?.location.href.includes("yourapp.com/callback")) {
                    // Assuming the callback URL is part of your app and can access the token
                    window.clearInterval(pollTimer);
                    authWindow.close();
                    // Here you would ideally extract the token from the URL
                    console.log('Authorization complete. Extract the token from the callback URL.');
                }
            } catch (error) {
                // Error handling, possibly due to cross-origin policies
                console.log('Error polling the authorization window:', error);
            }
        }, 1000);

        console.log('Get authorization action initiated.');
    };

    return (
        <section className={styles.settingsSection}>
            <table className={styles.settingsTable}>
                <tbody>
                    <tr>
                        <td>
                            <label htmlFor="clearCache" className={styles.label}>Cache</label>
                        </td>
                        <td>
                            <Button onClick={handleClearCache}>Clear Cache</Button>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <label htmlFor="themeSwap" className={styles.label}>Theme</label>
                        </td>
                        <td>
                            <Button onClick={handleChangeTheme}>Change Theme</Button>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <label htmlFor="getAuth" className={styles.label}>Auth</label>
                        </td>
                        <td>
                            <button onClick={handleGetAuthorization}>Get Authorization</button>
                        </td>
                    </tr>
                </tbody>
            </table>
        </section>
    );
};

export default Settings;
