import React, { useState } from 'react';
import styles from './Main.module.css';
import Button from '../../ui/button/Button';
import Input from '../../ui/input/Input';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCompactDisc, faMusic, faClock, faHourglassEnd } from '@fortawesome/free-solid-svg-icons';

const Main = () => {
    const [formData, setFormData] = useState({
        artist: '',
        album: '',
        track: '',
        startTimestamp: '',
        endTimestamp: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        // Add any submit logic or API call here
    };

    return (
        <section>
            <h2>Home</h2>
            <form onSubmit={handleSubmit} className={styles.form}>
                <table className={styles.inputTable}>
                    <tbody>
                        {/* Map through formData keys to generate input rows */}
                        {Object.entries(formData).map(([key, value]) => (
                            <tr key={key}>
                                <td><FontAwesomeIcon icon={key.includes('Timestamp') ? (key.startsWith('start') ? faClock : faHourglassEnd) : (key === 'artist' ? faUser : (key === 'album' ? faCompactDisc : faMusic))} /></td>
                                <td>{key.charAt(0).toUpperCase() + key.slice(1).replace('Timestamp', ' Time')}</td>
                                <td>
                                    <Input
                                        id={key}
                                        type={key.includes('Timestamp') ? 'datetime-local' : 'text'}
                                        name={key}
                                        value={value}
                                        onChange={handleChange}
                                        placeholder={`Enter ${key}`}
                                    />
                                </td>
                            </tr>
                        ))}
                        <tr>
                            <td colSpan="3" style={{ textAlign: 'right' }}>
                                <Button type="submit">Submit</Button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
        </section>
    );
};

export default Main;
