import React from 'react';
import styles from './Sidebar.module.css';
import Button from '../../ui/button/Button';

const Sidebar = () => {
    // Define an array of buttons with their paths and labels
    const buttons = [
        { path: "/", label: "Home" },
        { path: "/about", label: "About" },
        { path: "/settings", label: "Settings" }
    ];

    return (
        <aside className={styles.sidebar} >
            {/* Map over the buttons array and render a Button component for each one */}
            {buttons.map(button => (
                <Button key={button.path} path={button.path}>{button.label}</Button>
            ))}
        </aside>
    );
};

export default Sidebar;
