// OptionList.tsx
import React from 'react';
import styles from './OptionList.module.css';
import Button from '../../ui/button/Button';

interface Option {
    name: string;
    // Additional properties as needed, based on your API response structure
}

interface OptionListProps {
    options: Option[];
    onSelect: (selectedOption: Option) => void; // Callback function for when an option is selected
}

const OptionList: React.FC<OptionListProps> = ({ options, onSelect }) => {
    const optionListRef = React.useRef<HTMLDivElement>(null);
    const [visibleOptions, setVisibleOptions] = React.useState(options.length);

    React.useEffect(() => {
        if (optionListRef.current) {
            const containerHeight = optionListRef.current.clientHeight;
            const optionHeight = 40 + 10; // Height of option + margin-bottom
            const numberOfOptions = Math.floor(containerHeight / optionHeight);
            setVisibleOptions(numberOfOptions);
        }
    }, [options.length]);

    return (
        <div className={styles.optionList} ref={optionListRef}>
            {options.slice(0, visibleOptions).map((option) => (
                <Button key={option.name} onClick={() => onSelect(option)} className={styles.optionButton}>
                    {option.name}
                </Button>
            ))}
        </div>
    );
};

export default OptionList;
