// FileName: Main.tsx
import React, { useState } from 'react';
import styles from './Main.module.css';
import Button from '../../ui/button/Button';
import Input from '../../ui/input/Input';
import OptionList from '../../ui/optionList/OptionList';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCompactDisc, faMusic, faClock, faHourglassEnd } from '@fortawesome/free-solid-svg-icons';
import TimeSelectionRow from '../../ui/timeSelectionRow/TimeSelectionRow';

interface FormData {
    artist: string;
    album: string;
    track: string;
    startTimestamp: string;
    endTimestamp: string;
}

const useForm = (initialState: FormData) => {
    const [formData, setFormData] = useState<FormData>(initialState);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prevState => ({ ...prevState, [name]: value }));
    };

    const handleOptionSelect = (type: keyof FormData, option: { name: string }) => {
        setFormData(prevState => ({ ...prevState, [type]: option.name }));
    };

    return { formData, handleChange, handleOptionSelect };
};

const Main: React.FC = () => {
    const initialState: FormData = {
        artist: '',
        album: '',
        track: '',
        startTimestamp: '',
        endTimestamp: '',
    };

    const { formData, handleChange, handleOptionSelect } = useForm(initialState);

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        // Simple XML packaging of formData
        const xmlData = `
<response>
    <artist>${formData.artist}</artist>
    <album>${formData.album}</album>
    <track>${formData.track}</track>
    <startTimestamp>${formData.startTimestamp}</startTimestamp>
    <endTimestamp>${formData.endTimestamp}</endTimestamp>
</response>
        `;
        console.log(xmlData); // For demonstration, replace with actual submission logic
    };

    // Form rendering with inputs and option selections
    return (
        <section className={styles.section}>
            <h2>Home</h2>
            <form onSubmit={handleSubmit} className={styles.form}>
                {/* Form fields with FontAwesomeIcon, Input, and OptionList components */}
                {/* Refer to the initial Main component for detailed form structure */}
            </form>
        </section>
    );
};

export default Main;
