import React, { useEffect, useState } from 'react';
import styles from './About.module.css';
import { decrypt } from '../../utils/HandleAuth';
import CryptoJS from 'crypto-js';

interface AboutProps {
    author?: string;
    contact?: string;
    userID?: string;
}

const About: React.FC<AboutProps> = ({ author = 'J Kang', contact = 'kangjacob1@gmail.com', userID: initialUserID }) => {
    const [userID, setUserID] = useState<string | null>(initialUserID || localStorage.getItem('userID') || null);

    useEffect(() => {
        const fetchUserName = async () => {
            const encryptedToken = sessionStorage.getItem('authToken');
            const token = encryptedToken ? decrypt(encryptedToken) : null;
            const apiKey = '053905e1fc8b0de378dc341a24ec68c7';
            const secret = 'a0c2b1ab664e5ea5ca735d57f4cdaaf8';

            const paramsString = `api_key${apiKey}methodauth.getSessiontoken${token}${secret}`;
            const apiSig = CryptoJS.MD5(paramsString).toString();

            if (token) {
                const url = `https://ws.audioscrobbler.com/2.0/?method=auth.getSession&token=${token}&api_key=${apiKey}&api_sig=${apiSig}&format=json`;

                try {
                    const response = await fetch(url);
                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }
                    const data = await response.json();

                    if (data.session && data.session.name) {
                        setUserID(data.session.name);
                        localStorage.setItem('userID', data.session.name);
                    }
                } catch (error) {
                    console.error("Failed to fetch session:", error);
                }
            }
        };

        // Only attempt to fetch the username if it's not already set from localStorage
        if (!userID) {
            fetchUserName();
        }
    }, [userID, initialUserID]);

    return (
        <section className={styles.aboutSection}>
            <h2>About</h2>
            <p>This application generates visual representations of Last.FM data. Developed by J Kang and open for public use.</p>
            <table>
                <tbody>
                    <tr>
                        <td>License:</td>
                        <td>GPLv3</td>
                    </tr>
                    <tr>
                        <td>Logged In User:</td>
                        <td>{userID || 'Not logged in'}</td>
                    </tr>
                    <tr>
                        <td>Author:</td>
                        <td>{author}</td>
                    </tr>
                    <tr>
                        <td>Contact:</td>
                        <td>{contact}</td>
                    </tr>
                </tbody>
            </table>
        </section>
    );
};

export default About;
