// src/components/About/About.tsx
import React from "react";
import styles from "./About.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faPenNib, faUser } from "@fortawesome/free-solid-svg-icons";
import HandleAuth from "../../utils/HandleAuth"; 

const About = () => {
    const { userID, error } = fetchUserName; 

   const renderUserInfo = () => {
        if (error) return <p className={styles.errorMessage}>Error: {error}</p>;
        if (!userID) return "Not logged in";
        return userID;
    };

    return (
        <section className={styles.aboutSection}>
            <h2>About</h2>
            <p>This application generates visual representations of Last.FM data. Developed by J Kang and open for public use. For more information, feel free to contact at kangjacob1@gmail.com.</p>
            <table className={styles.iconTable}>
                <tbody>
                    <tr>
                        <td><FontAwesomeIcon icon={faUser} /></td>
                        <td>Logged In User:</td>
                        <td>{renderUserInfo()}</td>
                    </tr>
                    <tr>
                        <td><FontAwesomeIcon icon={faPenNib} /></td>
                        <td>Author:</td>
                        <td>J Kang</td>
                    </tr>
                    <tr>
                        <td><FontAwesomeIcon icon={faEnvelope} /></td>
                        <td>Contact:</td>
                        <td>kangjacob1@gmail.com</td>
                    </tr>
                </tbody>
            </table>
            {error && <p className={styles.errorMessage} role="alert">Error: {error}</p>}
        </section>
    );
};

export default About;
