import React, { useState } from 'react';
import styles from './Main.module.css'; // Ensure this path matches your file structure

const Main = () => {
    const [formData, setFormData] = useState({
        artist: '',
        album: '',
        track: '',
        startTimestamp: '',
        endTimestamp: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    return (
        <section className={styles.formContainer}>
            <form onSubmit={handleSubmit}>
                <table>
                    <tbody>
                        <tr>
                            <th><label htmlFor="artist">Artist</label></th>
                            <td><input id="artist" name="artist" type="text" value={formData.artist} onChange={handleChange} className={styles.inputField} /></td>
                        </tr>
                        <tr>
                            <th><label htmlFor="album">Album</label></th>
                            <td><input id="album" name="album" type="text" value={formData.album} onChange={handleChange} className={styles.inputField} /></td>
                        </tr>
                        <tr>
                            <th><label htmlFor="track">Track</label></th>
                            <td><input id="track" name="track" type="text" value={formData.track} onChange={handleChange} className={styles.inputField} /></td>
                        </tr>
                        <tr>
                            <th><label htmlFor="startTimestamp">Start Time</label></th>
                            <td><input id="startTimestamp" name="startTimestamp" type="datetime-local" value={formData.startTimestamp} onChange={handleChange} className={styles.inputField} /></td>
                        </tr>
                        <tr>
                            <th><label htmlFor="endTimestamp">End Time</label></th>
                            <td><input id="endTimestamp" name="endTimestamp" type="datetime-local" value={formData.endTimestamp} onChange={handleChange} className={styles.inputField} /></td>
                        </tr>
                        <tr>
                            <td style={{ textAlign: 'center' }}>
                                <button type="submit" className={styles.button}>Generate</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
            <div id="results" className={styles.results}>
            </div>
        </section>
    );
};
export default Main;
