import React, { createContext, useContext, useState } from 'react';

// Create a context for the theme
const ThemeContext = createContext();

export const useTheme = () => useContext(ThemeContext);

export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState('light'); // Default theme

    const toggleTheme = () => {
        setTheme(theme === 'light' ? 'dark' : 'light');
    };

    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>
            {children}
        </ThemeContext.Provider>
    );
};
    const Settings = () => {
        // Use the context
        const { toggleTheme } = useTheme();
  return (
    <section>
      <table>
        <tbody>
          <tr>
            <td>
              <label htmlFor="clearCache">Cache</label>
            </td>
            <td>
              <button id="clearCache" onClick={handleClearCache}>Clear Cache</button>
            </td>
          </tr>
          <tr>
            <td>
              <label htmlFor="themeSwap">Theme</label>
            </td>
            <td>
              <button id="themeSwap" onClick={handleChangeTheme}>Change Theme</button>
            </td>
          </tr>
          <tr>
            <td>
              <label htmlFor="getAuth">Auth</label>
            </td>
            <td>
              <button id="getAuth" onClick={handleGetAuthorization}>Get Authorization</button>
            </td>
          </tr>
        </tbody>
      </table>
    </section>
  );
};

export default Settings;
