// App.tsx
import React, { useEffect } from 'react';
import styles from './App.module.css';
import { BrowserRouter as Router } from 'react-router-dom';
import Container from './ui/container/Container';

function App() {
    useEffect(() => {
        const handleMessage = (event) => {
            // Check the origin of the message for security purposes
            if (event.origin !== window.location.origin) {
                return; // Ignore messages from unknown sources
            }

            // Assuming the message contains the token or a signal of successful authentication
            console.log('Message from popup:', event.data);
            // Here, you can handle the authentication token, update app state, etc.

            // Optionally, close the popup if you have a reference to it
        };

        // Listen for messages
        window.addEventListener('message', handleMessage);

        // Cleanup the event listener on component unmount
        return () => {
            window.removeEventListener('message', handleMessage);
        };
    }, []);

    return (
        <div className={styles.appContainer}>
            <Router>
                <Container />
            </Router>
        </div>
    );
}

export default App;
