import React, { useEffect, useState } from 'react';
import styles from './About.module.css'; // Make sure this path is correct

const About = ({ author = 'J Kang', contact = 'kangjacob1@gmail.com' }) => {
    const [userID, setUserID] = useState('');

    useEffect(() => {
        const fetchUserName = async () => {
            const token = 'your_token_here'; // Replace with your token
            const apiKey = '053905e1fc8b0de378dc341a24ec68c7'; // Replace with your API key
            const apiSig = 'a0c2b1ab664e5ea5ca735d57f4cdaaf8'; // Replace with your function to get API signature

            const response = await fetch(`https://ws.audioscrobbler.com/2.0/?method=auth.getSession&token=${token}&api_key=${apiKey}&api_sig=${apiSig}&format=json`);
            const data = await response.json();

            setUserID(data.session.name); // Set the username
        };

        fetchUserName();
    }, []);

    return (
        <section className={styles.aboutSection}>
            <h2>About</h2>
            <p>This application generates visual representations of Last.FM data. Developed by J Kang and open for public use. </p>
            <table>
                <tbody>
                    <tr>
                        <td>License:</td>
                        <td>GPLv3</td>
                    </tr>
                    <tr>
                        <td>Logged In User:</td>
                        <td>{userID}</td>
                    </tr>
                    <tr>
                        <td>Author:</td>
                        <td>{author}</td>
                    </tr>
                    <tr>
                        <td>Contact:</td>
                        <td>{contact}</td>
                    </tr>
                </tbody>
            </table>
        </section>
    );
};

export default About;
