// File: src/components/Container/Container.tsx
import React, { useContext } from "react";
import { Routes, Route } from "react-router-dom";
import Header from "../../ui/header/Header";
import Sidebar from "../../ui/sidebar/Sidebar";
import Main from "../../components/Main/Main";
import About from "../../components/About/About";
import Settings from "../../components/Settings/Settings";
import Footer from "../../ui/footer/Footer";
import styles from "./Container.module.css";
import { AuthContext, useHandleAuth } from "../../utils/HandleAuth";

interface ContainerProps {
  curHost?: "Main" | "Settings" | "About"; // Made optional until usage is clarified
  localCache?: "true" | "false" | null; // Made optional until usage is clarified
}

const Container: React.FC<ContainerProps> = () => {
  const authContext = useContext(AuthContext);
  const userID = authContext?.userID;

  return (
    <div className={styles.appContainer}>
      <Header />
      <div className={styles.contentWrapper}>
        <Sidebar />
        <main className={styles.mainContent}>
          <Routes>
            <Route path="/" element={<Main />} />
            <Route path="/about" element={<About userID={userID} />} />
            <Route path="/settings" element={<Settings />} />
          </Routes>
        </main>
      </div>
      <Footer />
    </div>
  );
};

export default Container;
