// File: src/components/Settings/Settings.tsx
import React from 'react';
import styles from './Settings.module.css';
import Button from '../../ui/button/Button';
import { encrypt } from '../../utils/encryption';

const Settings = () => {
    const settingsOptions = [
        {
            id: 'clearCache',
            label: 'Cache',
            action: () => console.log('Clear cache action'),
        },
        {
            id: 'themeSwap',
            label: 'Theme',
            action: () => console.log('Change theme action'),
        },
        {
            id: 'getAuth',
            label: 'Auth',
            action: () => {
                const apiKey = '053905e1fc8b0de378dc341a24ec68c7'; // Assume this is set in your .env file
                const callbackUrl = `https://www.last.fm/api/auth/?api_key=${apiKey}`;
                window.open(callbackUrl, "_blank");
                console.log('Get authorization action initiated.');
            },
        },
    ];

    return (
        <section>
            <h2>Settings</h2>
            <table className={styles.settingsTable}>
                <tbody>
                    {settingsOptions.map(option => (
                        <tr key={option.id}>
                            <td>
                                <label htmlFor={option.id} className={styles.label}>{option.label}</label>
                            </td>
                            <td>
                                <Button onClick={option.action}>{option.label}</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default Settings;
