import React, { useState, useEffect } from 'react';
import styles from './Main.module.css';
import Button from '../../ui/button/Button';
import Input from '../../ui/input/Input';
import OptionList from '../../ui/optionList/OptionList';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCompactDisc, faMusic, faClock, faHourglassEnd } from '@fortawesome/free-solid-svg-icons';

const Main = () => {
    const [formData, setFormData] = useState({
        artist: '',
        album: '',
        track: '',
        startTimestamp: '',
        endTimestamp: '',
    });

    const [options, setOptions] = useState({
        artists: [],
        albums: [],
        tracks: [],
    });

    const fetchAPI = async (method) => {
        const apiKey = "YOUR_API_KEY"; // Replace this with your actual API key
        const user = localStorage.getItem('userID'); // Assuming 'userID' is stored in localStorage
        const url = `https://ws.audioscrobbler.com/2.0/?method=${method}&user=${user}&api_key=${apiKey}&format=json`;

        try {
            const response = await fetch(url);
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            return data;
        } catch (error) {
            console.error("Fetch error: ", error);
            return null;
        }
    };

    useEffect(() => {
        const fetchData = async () => {
            const artistsData = await fetchAPI('user.gettopartists');
            const albumsData = await fetchAPI('user.gettopalbums');
            const tracksData = await fetchAPI('user.gettoptracks');

            // Assuming the API returns the list under a property, e.g., topalbums, topartists, toptracks
            setOptions({
                artists: artistsData?.topartists?.artist ?? [],
                albums: albumsData?.topalbums?.album ?? [],
                tracks: tracksData?.toptracks?.track ?? [],
            });
        };

        fetchData();
    }, []);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleOptionSelect = (type, value) => {
        setFormData(prevState => ({
            ...prevState,
            [type]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    return (
        <section>
            <h2>Home</h2>
            <form onSubmit={handleSubmit} className={styles.form}>
                <table className={styles.inputTable}>
                    <tbody>
                        <tr>
                            <td><FontAwesomeIcon icon={faUser} /></td>
                            <td>Artist</td>
                            <td>
                                <Input
                                    id="artist"
                                    type="text"
                                    name="artist"
                                    value={formData.artist}
                                    onChange={handleChange}
                                    placeholder="Enter artist name"
                                />
                            </td>
                            <td>
                                <OptionList options={options.artists} onSelect={(value) => handleOptionSelect('artist', value)} />
                            </td>
                        </tr>
                        <tr>
                            <td><FontAwesomeIcon icon={faCompactDisc} /></td>
                            <td>Album</td>
                            <td>
                                <Input
                                    id="album"
                                    type="text"
                                    name="album"
                                    value={formData.album}
                                    onChange={handleChange}
                                    placeholder="Enter album title"
                                />
                            </td>
                            <td>
                                <OptionList options={options.albums} onSelect={(value) => handleOptionSelect('album', value)} />
                            </td>
                        </tr>
                        <tr>
                            <td><FontAwesomeIcon icon={faMusic} /></td>
                            <td>Track</td>
                            <td>
                                <Input
                                    id="track"
                                    type="text"
                                    name="track"
                                    value={formData.track}
                                    onChange={handleChange}
                                    placeholder="Enter track name"
                                />
                            </td>
                            <td>
                                <OptionList options={options.tracks} onSelect={(value) => handleOptionSelect('track', value)} />
                            </td>
                        </tr>
                        <tr>
                            <td><FontAwesomeIcon icon={faClock} /></td>
                            <td>Start Time</td>
                            <td>
                                <Input
                                    id="startTimestamp"
                                    type="datetime-local"
                                    name="startTimestamp"
                                    value={formData.startTimestamp}
                                    onChange={handleChange}
                                    placeholder="Start timestamp"
                                />
                            </td>
                        </tr>
                        <tr>
                            <td><FontAwesomeIcon icon={faHourglassEnd} /></td>
                            <td>End Time</td>
                            <td>
                                <Input
                                    id="endTimestamp"
                                    type="datetime-local"
                                    name="endTimestamp"
                                    value={formData.endTimestamp}
                                    onChange={handleChange}
                                    placeholder="End timestamp"
                                />
                            </td>
                        </tr>
                        <tr>
                            <td></td>
                            <td></td>
                            <td>
                                <Button type="submit">Submit</Button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
            <div id="results" className={styles.results}>
            </div>
        </section>
    );
};

export default Main;
