// FileName: HandleAuth.tsx
import { createContext, useState, useEffect, useCallback, useContext } from 'react';
import CryptoJS from 'crypto-js';

const secretKey = 'a0c2b1ab664e5ea5ca735d57f4cdaaf8';

const HandleAuthContext = createContext<{
    userID: string | null;
    error: string | null;
    getAuth: () => string | null;
    setAuth: (token: string) => void;
    checkAndModifyDOMBasedOnUser: () => void;
} | undefined>(undefined);

export const HandleAuthProvider = ({ children }: { children: React.ReactNode }) => {
    const [userID, setUserID] = useState<string | null>(localStorage.getItem('userID'));
    const [error, setError] = useState<string | null>(null);

    const encrypt = useCallback((text: string): string => CryptoJS.AES.encrypt(text, secretKey).toString(), []);

    const decrypt = useCallback((ciphertext: string): string => {
        const bytes = CryptoJS.AES.decrypt(ciphertext, secretKey);
        return bytes.toString(CryptoJS.enc.Utf8);
    }, []);

    const setAuth = useCallback((token: string) => {
        const encryptedToken = encrypt(token);
        sessionStorage.setItem('authToken', encryptedToken);
        fetchUserName();
    }, [encrypt]);

    const getAuth = useCallback(() => userID, [userID]);

    const fetchUserName = useCallback(async () => {
        const encryptedToken = sessionStorage.getItem('authToken');
        const token = encryptedToken ? decrypt(encryptedToken) : null;
        const apiKey = '053905e1fc8b0de378dc341a24ec68c7';

        if (token) {
            const paramsString = `api_key=${apiKey}&token=${token}`;
            const apiSig = CryptoJS.MD5(paramsString + secretKey).toString();
            const url = `https://ws.audioscrobbler.com/2.0/?method=auth.getSession&token=${token}&api_key=${apiKey}&api_sig=${apiSig}&format=json`;

            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();

                if (data.session && data.session.name) {
                    setUserID(data.session.name);
                    localStorage.setItem('userID', data.session.name);
                    sessionStorage.setItem('userID', data.session.name);
                }
            } catch (error) {
                setError(error instanceof Error ? error.message : String(error));
            }
        }
    }, [decrypt, setUserID, setError]);

    useEffect(() => {
        if (!userID) {
            fetchUserName();
        }
    }, [userID, fetchUserName]);

    return (
        <HandleAuthContext.Provider value={{ userID, error, getAuth, setAuth, checkAndModifyDOMBasedOnUser: () => { } }}>
            {children}
        </HandleAuthContext.Provider>
    );
};

export const useHandleAuth = () => {
    const context = useContext(HandleAuthContext);
    if (context === undefined) {
        throw new Error('useHandleAuth must be used within a HandleAuthProvider');
    }
    return context;
};
